                {{-- Pagination Elements --}}
                @foreach ($elements as $element)
                    {{-- "Three Dots" Separator --}}
                    @if (is_string($element))
                        <span class="join-item btn btn-disabled" aria-disabled="true">{{ $element }}</span>
